(21, 22, 1, 'منك ايا برهان يا فتاح', 'منك ايا برهان  يا فتاح* بزبد المعارف استفتاح\nانت تلوت سبح اسم ربكا* في سره امطرت غيث سحبكا\nوطالب قلت له سنقرئك*  انت فلا تنسى ونحن ننبئك\nبالنبأالعظيم حتى تعرفه*فترتقي في درجات المعرفة\nتلوت سورة وتلك الفاتحة*نفسك اضحت للمعاني مانحة\nومعنى اهدنا الصراط المستقيم* بينته فساغ في العقل السليم\nوسورة الرحمن قد تلوتها*لطالب اسرارها حبوتها\nمعنى السماوالارض في يسئله*او ضحته لكل من يعقله\nابيات سيف الدين قدضمنتها*بين عبارات بها زينتها\nوبعدتضمين لقدشرحتها*  بخير اسلوب لقد اوضحتها\nرسالة وهي الصلوة الاولى*  تلك عليك نزلت نزولا\nوانها صلوة  الاوابينا*  وللمعاد ذخر التوابينا\nسلام رب العرش والصلوة * عليك ما قد قامت الصلوة ', 'ارجوزة', 'ARB', '13.0', '', '', '', '2025-05-29 19:55:29', '', '0'),
(22, 23, 1, 'هذي موائد سيفية لنا معروفة', 'هذي موائد سيفـ*ـية لنا معروفة  \n وبالملا ئكة الطهـ*ـر قد غدت محفوفة\nوبالنظافة  في النا*س اصبحت موصوفة\nيطعمنا مولانا*من الطعام صنوفه\nوثمرات علوم الـ*ـهدى به مقطوفة\nوفي المحرم دابا*يطعم فضلا ضيوفه\nيجلسهم هو فو*ق النمارق المصفوفة\nعلى كرامتهم قد*  ينفق منا الوفه\nالى طعام علي*نادى المنادي بكوفة\nاسرار طيبه في* الستر به مكشوفة\nالبدريدفع عنا*من الزمان صروفه\nويستغيث به مل*هوف كذا ملهوفة\nان الدعاة هم للا*مام اضحوا سيوفه\nبهارؤس العدى عن*  اجسامهم محذوفة\nموائد هي مأنو*سة لنا مألوفة ', 'ف', 'ARB', '15.0', '', '', '', '2025-05-29 19:31:02', '', '0'),
(23, 24, 1, 'قلب قد جئت الى انجلترا', 'قلب قد جئت  الى  انجلترا*  ههنا القرآن والانجيل  ترى\nانما القرآن والا نجيل نجـ*ـل لسيف الدين من غير امترا\nمن يقل ليس كسيف الدين بر*  هانه فهو على الله افترى\nنحن بايعناه صدقا فهو بالـ*ـجنة الا نفس والمال اشترى\nان برهان الهدى قد ناب عن*طيب العصر اذاما استترا\nوعلى كرسيه اجلسه*نحوه فوض منه الدفترا\nواليه نحن قد نثني اعـ*ـنتنا حين مهم  اعترى\nانه سلمان اهل البيت في الـ*ـعلم ضاهى في سطاه الاشترا\nكعلي يقتل الاضداد عمـ*ـرو بن ود  مرحبا ا و عنترا\nانه الكوثر حقا ان شا* نئه لاريب اضحى ابترا\nيوم ميلاد لبرهان الهدى*  مؤمن فيه  سرو را بخترا\nللتهاني كل عبد قد اتا*  ه  بعلياه غدى  مستهترا\nوصلوة الله تغشى المرتضى * لعن الله د لا ما، حبترا  ', 'ر', 'ARB', '13.0', '', '', '', '2025-06-03 17:13:38', '', '0'),
(24, 25, 1, 'يا رؤفا بنا علينا حريصا', 'يا رؤفا بنا علينا حريصا*واهبا من تقوى الاله قميصا\r\nداعي الله عم جودك لكن* انت خصصتنا به تخصيصا\r\nوباخلا صنا لحبك من بحـ*ـر الهيولى خلصتنا تخليصا\r\nانما علم آل فاطمة غا*ل ثمين ما كان شيئا رخيصا\r\nوشرحت القرآن شرحالنايشـ*ـفي غليلا لخصته تلخيصا\r\nيا حكيما قد يعرف السقم من مر*ضاه جسا لنبضهم تشخيصا\r\nنال مرضاة ربه حينما شـ*ـوص  مرء اسنانه تشويصا\r\nلم يرخص الهنا احدا في*  ترك حب الداعي الرضى ترخيصا\r\nانما ضده ابولهب اذ*لم يجد من نار الجحيم محيصا\r\nومحب له ترى فيه رهبا*  نية  بطنه يكون خميصا\r\nظله وارف فمن جاءه ما* نغص الله عيشه تنغيصا\r\nوهو انسان كامل ذوالمعالي* ناقص من نقصه تنقيصا\r\nصار سهلا تصورالعلم من تبـ*ـيينه قبل كان عندي عويصا  ', 'ص', 'ARB', '13.0', '', '', '', '2025-06-03 17:14:56', '', 'mmmurtaza@gmail.com'),
(25, 26, 1, 'نور دين الهدى لك الجبروت', 'نور دين الهدى لك الجبروت*  لك مولاي الملك والملكوت\nانت عينت ارض قبرك لا تعـ*ـلم نفس باي ارض تموت\nواذا بالغ اليك كلا م*  عندذاواجب علينا السكوت\nانت ابطلت السحر حين اراد الـ*ـسحر هاروت العصر او ماروت\nوبظلم ادخلك الحبس فرعو*ن زمان وذالك الطاغوت\nوورثت الآ باء عندك مجمو*عا غدى كل ما هو المشتوت\nومناخ دارك  زادك لا ريـ*ـب مباح  ينال منه القوت\nنعرف الحق قالوا كيف فقلنا*ذات نور الهدى لدينا الثبوت\nملكا اصطفاه مولاه للنا*س كما صار مصطفى طالوت\nهو داؤدنا اذا ما رمى سهـ*ـما صريعا لقد غدى جالوت\nونرى في محمد نور حق*هو في خاتم الهدى ياقوت\nانما اوهن البيوت لبيت*  المبغض الضد اذ هو العنكبوت\nطول الله عمر برهان دين*عمرت منه للاله بيوت\nربنا احفظ عراقنا افغانسـ * ـتاننا لبنانا بها بيروت\nوصلوة على بني المصطفى طه * الرضى ما في القجر يتلى القنوت ', 'ت', 'ARB', '15.0', '', '', '', '2025-05-29 20:13:24', '', '0'),
(26, 27, 1, 'انت  حفظا  اسمعتنا القرآنا', ' انت حفظا اسمعتنا القرآنا \nياحسينا فزدتنا ايمانا\n\nانت في عرس النجم كالكوكب الدر\nي اشرقت في السما لمعانا\n\nما در ينا أ نسمع الذكر ام من \nعالم القدس نسمع الألحانا\n\nاذ قرأت القرآ ن ذا كله حركت\nمنا القلوب والأذهانا\n\nسر داعي الهدى بذا الحفظ اذقد\nجئت حقا لقلبه ريحانا\n\nعند اهل النهى هو الملك القد \nسي لو ظاهرا يرى انسانا\n\nوتهنيك اليوم جامعة سي\nفية بالخشوع يا مولانا\n\nوتهني حسينها واباه \nالفاضل البر الأرؤف الحنا نا\n\nانت حفظا اسمعتنا القرآنا \nياحسينا فزدتنا ايمانا\n\nانت في عرس النجم كالكوكب الدر\nياشرقت في السما لمعانا\n\nما در ينا أ نسمع الذكر ام من \nعالم القدس نسمع الألحانا\n\nاذ قرأت القرآ ن ذا كله حركت\nمنا القلوب والأذهانا\n\nسر داعي الهدى بذا الحفظ اذ قد\nجئت حقا لقلبه ريحانا\n\nعند اهل النهى هو الملك القد \nسي لو ظاهرا يرى انسانا\n\nوتهنيك اليوم جامعة سي\nفية بالخشوع يا مولانا\n\nوتهني حسينها واباه \nالفاضل البر الأرؤف الحنانا', 'ن', 'ARB', '16.0', '', '', '', '2025-06-03 17:17:54', 'clean up needed', '0'),
(27, 29, 1, 'دمعي جرى بانسكاب', 'دمعي جرى بانسكاب*قلبي غدى في اضطراب\nأحزنني موت الشيـ*ـخ طاهر المعقابي\nوشيعته دموع*جرت من الأحباب\nونفسه في جنان*وجسمه في تراب\nدعو له دائما عنـ*ـد ربنا التواب\nبرهان دين ضمين*له بحسن مآب\nلذته كانت في*تدريس كتب النصاب\nأحب شيئ لديه*مطالعات الكتاب\nفهبه من خدمة ربـ*ـنا عظيم الثواب\nوأعطه يا إلهي*أجرا بغير حساب\nطول بقاء لداعي الـ * ـهدى مدى الأحقاب ', 'ب', 'ARB', '11.0', '', '', '', '2025-05-29 19:32:24', '', '0'),
(28, 33, 1, 'ان بسم الله اصل البركة', 'ان بسم الله اصل البركة*  اهله لا ريب اهل البركة\rمن يسمي الله من يحمده*اكله والله اكل البركة\rواذاقبل طعام  بعده*غسل الايدي فغسل البركة\rواذا ما كثر الايدي فمجـ*ـتمعاقدصارشمل البركة\rواذا كان حلا لا فزكى*ونمى لا شك حقل البركة\rمن يكن مثل ابن الكوا اكله*نحوه ينسب جهل البركة\rوببرهان الهدى قدوضحت*لبني الايمان سبل البركة\rمن يسبح باسم داعي عصره*فله يثمر نخل البركة\rان من يعرض عنه فله*لم يكن يفتح قفل البركة\rلم يزل برهان دين زهرة*نحوه يقصد نحل البركة\rمن يذق قبل طعام  بعده*الملح ذوقافهو فعل البركة\rلم يزل متصلا من آل طـ*ـه بداعي الحق حبل البركة\rولنا الكفلان كفل الخيرثـ*ـم  لنا يحصل كفل البركة\rوبعالي القدر قد يفرغ في*  طلباء العلم سجل البركة\rصادق من نسل برهان الهدى * نسله الطاهر نسل البركة\r\r\n\r\nلعين مولاك جئت قرة* اتيت للخادمين غرة\rيا قائدا جوهرا ثمينا* في صدف الدين انت درة\rابوك داعي الهدى وامـ*ـك ام منعامة وبرة\rبوصاحبة امة الله آئي قد*  ولدت من بطن هذي الحرة\rفقدس الله روحهاقد*  اقول ذا القول الف مرة\rوطول الله عمرداعي الـ*ـزمان ابقاه في المسرة\rالعبد ما اسطاع مدح المولى * فاين شمس واين ذرة\r\n\r\n\r\n\r\n\rان بســم الله اصـــــل البــركـــة     اهــــلــه لا ريــب اهـــل البـــركــــة\rمن يسمي الله من يـــحـمــــده      اكــلـــــه والله اكـــــــــــل البـركــــــــة\rواذاقـــبل طـــعــام  بـــعـــــــده      غـسل الايـدي فــغــــسل البــركـــة\rواذا ما كـثــــر الايدي فـــمـــجـــــــــــــتـمـعـــاقــــــدصارشمـل البـــركـــة\rواذا كـان حــــــلا لا فـــــــزكـى      ونمــى لا شك حـقــــــل البـركـــة\rمن يكــن مثل ابن الكــوا اكـلـه      نحــــوه ينـسب جـهــــــل البـركــة\rوبــبـرهان الهدى قدوضحت      لبـني الايـــمــان سبــــــل البـركـــة\rمن يسبح باسم داعي عصره      فــلـه يـثــــمــــر نخــــــــل البـركــــة\rان من يـعــرض عـــنـه فــلـــــه      لم يكـــن يفــتـــح قـفــــل البــركــــة\rلم يـزل بـــرهــان ديــن زهــرة      نحــــــوه يـقــــصـد نحـــل البـركـــــة\rمن يـذق قـبـل طعــام  بعــده      المـلــح ذوقافـهـو فـعــــل البـركــــة\rلـم يـــزل متـــصـلا مـــن آل طـــــــــــــه بداعي الحق حـبــل البـركـــــة\rولنا الكــفــلان كــفـل الخيـرثــــــــــــــــم  لنا يـــحصـل كــفـل البـركــــــة\rوبعـــالي القــدر قد يـفــرغ في     طـلـباء الــعـلـم سجـل البـركــــــة\rصادق من نسل برهان الهدى  نسله الطاهـر نسل البـركــة', 'ك', 'ARB', '15.0', '', '', '', '2025-05-29 17:54:28', 'doubled bijo qasido wachma che', '0'),
(29, 34, 1, 'أتى برهان دين للعيادة', 'أتى برهان دين للعيادة* كليم الدين قد حزت السعادة\nدعى لك بالشفا ودعاءه مسـ*ـتجاب لامرا في  شاهزاده\nعلى قدميك سوف نراك تمشي*وتحضر في المساجد للعبادة\nوتعرض بالفصاحة عند مولا	* ك هذا العرض حقا خير عادة\nوانت من المفيدة ياكليم الـ*ـرشاد افدتنا فضلا افادة\nلقد قلدتنا باللطف سمط الـ*ـحقائق انه نعم القلادة\nامام الحق بيت الله في ظلـ*ـه ثنيت لداعيه الوسادة\nله خضع الملوك اذا على را*سه الأعلى رأوا تاج السيادة\nينوح على الحسين فأعطه يا* الهي عمر نوح بل زيادة ', 'د', 'ARB', '8.0', '', '', '', '2025-06-03 17:19:15', '', '0'),
(30, 37, 1, 'لقيتني من بعد أن فارقتني', 'لــقــيتــني مـن بـعد أن فــــــارقـتـني  *     غـــــادة  كــم كـــأس الرضاب سقتـني\nانهاقد أهدت الى القـــلب والنفـــــــــ*ـــــــــس المسرات بـــعــــــد أن  أقــلــقــتـــني\nقيل ان الجزاء من جنس أعــــمـــا   *     ل فـــلما عـــــشقتـــها عـــشــــقــــتــني\nنظرات من سيف دين الهدى المو  *     لى المفدى مـــــن أسرها أطــلقــــتني\nانني كــنت في عــبـــــــوديـــــــــــة الدنــ*ــــــــــــــــــــيا فـــمنها فـــيــــــــــوضه أعـتقتني\nاظــلمتني الدنيا بزبــرجـــــــها الفــا  *      ني فـــأنــــواره لـــقــــد  أشـــــــــرقــتني\nوالى الشر مــلت  نــــظــــرة برها    *     ن الهـــدى نــحو الخـــــير قـــد وفقتني\nانــــه انــــه الغــــياث هــــــــو الملـــــ*ــــــــــــــــــجا  اذا مــــا الذنــــــوب قـــد أوبقتني\nانمــــــــا ذاتــــــه الكـــــــريمة أرزا   *      ق عـــلـــوم قـــــــدســـــــية رزقــــــتـني\nوأتت مـــــن لدنه أدعـية فـــــــــــهــــ*ــــــــــــــي وقت أهــــــــل وده ووقــــــــــتـــــني\nصلــــــوات عـــــلى الـــنبي وذر\nيته  نحــــــــو سوحهم شوقــتني\n\n\n\n\nلــقــيتــني مـن بـعد أن فــــــارقـتـني       غـــــادة  كــم كـــأس الرضاب سقتـني\nانهاقد أهدت الى القلب والنفــــــــــــــــــس المسرات بـــعــــــد أن  أقــلــقــتني\nقيل ان الجزاء من جنس أعــــمـــا        ل فـــلما عـــــشقتـــها عـــشــــقــــتــني\nنظرات من سيف دين الهدى المو       لى المفدى مـــــن أسرها أطــلقــــتني\nانني كــنت في عــبوديـــــــــــة الدنــــــــــــــــــــــيا فـــمنها فـــيوضه أعـتقتني\nاظــلمتني الدنيا بزبــرجـــــــها الفــا        ني فـــأنــــواره لـــقــــد  أشـــــــــرقــتني\nوالى الشر مــلت  نــــظــــرة برها         ن الهـــدى نــحو الخـــــير قـــد وفقتني\nانــــه انــــه الغــــياث هــــــــو الملـــــــــــــــــــــــجا  اذا مــــا الذنــــــوب قد أوبقتني\nانمــــــــا ذاتــــــه الكـــــــريمة أرزا         عــــــــلـــوم قـــــــدســـــــية رزقــــــتـني\nوأتت مـــــن لدنه أدعـية فـــــــــــهــــــــــــــــــي وقت أهــــــــل وده ووقــــــــــتـــــني\nصلــــــوات عـــــلى ا