(52, 60, 1, 'جاء لاستقبال ميلادك يوم الجمعة', 'جاء لاستقبال ميلادك يوم الجمعة*معك المختار طه المصطفى انت معه\rمكرمات للدعاة المطلقين الكرمأ* هي في ذاتك برهان الهدى مجتمعة\rنص طه الطهر في خم على المولى علـ*ـي فهذا طول دهر سنة متبعة\rانت تاج الدعوة الغراءفيه جوهرا*غاليامن نصه سيف الهدى قدرصعه\rبانقطاع النص قالت فئة هاانها* لامرا من بركات قد غدت منقطعة\rان اسرار الامام المستقرالطيب الـ*ـطهرفي قلبك حقا اصبحت مستودعة\rضده نلعن لعنا مثل ما قبل معا*  وية يلعنه الليث الجري صعصعة', 'ع', 'ARB', '7.0', '', '', '', '2025-05-29 18:23:42', '', '0'),
(53, 61, 1, 'ابرهان دين ايا داعيا نحو آل علي ايا ذا المعالي', 'ابرهـــــان دين اياداعــــيا نحو آل علي ايا ذاالمعـــالي\nرأينا لأنوار طيب عصر بوجهك اشراقها والتلالي\n\nحباك الـه السما نورقـــــــــدس فليس لبدر وليس لشمس\nتحاكيك في النور كلا ولا في الكمال وفي الحسن او في الجمال\n\nوانك معطي السؤالات اعطيتناخير مافي خـزائنك ال\nعظيمة حبك حب الدعاة الكرام البهاليل قبل السؤال\n\nوانـــك جاهـــــــــدت في الله لله مثل رجـــال تقـاة كــــــماة\nلقد صدقوا ما هم عاهدوه عليه فاكرم بهم مــن رجــال\n\nولازلت تبكي بني دين حق على سيدالشهداءحسين\nفندعو لطـــــــــول بقاءك دابا على مـــــــر ايامنا والليالي\n\nوفي خطب انت تذكر خطب الحسين بحرقة قـلب كـئيب\nوفي كل نـظم نـظمت نـــــثرت الدمــــــوع عليه كـنثــر اللؤالي\n\nالهي فالعن يزيدا وشمرا ، معـــوية الرجس وابن زيــاد\nكذلك مروان ثم ابـن مرجانـة وابــن تيمية والغــــــزالي ', 'ل', 'ARB', '7.0', '', '', '', '2025-05-29 20:02:10', '', '0'),
(54, 62, 1, 'قد رأينا اعلى شؤن محمد', 'قد رأينا اعلى شؤن محمد* لامراحاز للفنون محمد\rفي عيون الاخبارذكر حسين* وقرأناه في عيون محمد\rان طلاب العلم قد اخذواالاو* راق للوعظ من غصون محمد\rلم يصل واصل الى علم اسما*  ء الاله الحسنى بدون  محمد\rنحن ندعو في كل يوم وليل*  دام فينا مدى القرون محمد\rملجأ في حيوتنا وملا ذ*  وغياث عند المنون  محمد\rبالعدى نحن لا نبالي فانا * قد اوينا الى حصون محمد', 'ن', 'ARB', '7.0', '', '', '', '2025-05-29 18:23:52', '', '0'),
(55, 63, 1, 'سمعنا منك البيان مولاي كان ظاهره انيقا', 'سمعنا منك البيان مولاي كان ظاهره انيقا*و عند اهل العقول والله كان باطنه عميقا\rابنت معنى مع النبيين والصديقين بالبيان*الواضح والشهداء والصالحين قد حسنوا رفيقا\rوهذه نعمة علينا انعمتها ايها الرسول*الذيّ يعطي لكل من قد يطيعه مجده العريقا\rنسجد في حضرة امامية من الشكر سجدات*وقد علمنا بان عشر العشير منه فلن نطيقا\rذكرت مولا تنا البتول الزهراء بنت النبي ذكرا*اجرى من العين دمعها وهو في الحشى اشعل الحريقا \rبكيت ابكيت حين اخرجت كل لفظ بحرقة في*شهادة بن الزهرا حسين ايا محبا له عشيقا\rيا داعيا للحسين والحسن الرضى كل مخطئ لو*غدى لحبك حاملا فهو لا مرا قد غدى عتيقا\rذكرت فاطمة مع المصطفى مع المرتضى ذكرت*الحسين  مظلوم كربلاء للمجتبى قد اتى شقيقا\rعيناك عينان في حسين نضاختان اسى ودمعا*فتنجيان لكل من في بحر الهيولى غدى غريقا\rودمت في صحة ويمن وكل سعد وكل خير*ادامنا في ظليل ظلك ربنا يا ابا شفيقا\rوان ابناءك السرى الطهر بعضهم لؤلؤ وبعض*زمرد ثم بعضهم جوهر وبعض اتى عقيقا\rيا رب صل على نبي الهدى وابناء بنته فا*طم وآباء طيب واسقنا بايديهم رحيقا', 'ق', 'ARB', '12.0', '', '', '', '2025-05-29 16:37:24', '', '0'),
(56, 64, 1, 'جاءنا اليوم ذا فخره واضح', 'جاءنا اليوم ذا فخره واضح*  وهو ابواب سعد لنا فاتح\rطيب آلاء الميلا د التاسع و* الثمانين بين الورى فائح\rولسيف الهدى طاهر انت يا*محمده عمل صالح\rوبحبك يا داعي الله في* القيامة ميزا ننا راجح\rوقبلت ضيافة جامعة*  وانت لها للمنى مانح\rان سادتنا الكرما كل وا*  حد منهم مشفق ناصح\rفازمن يبذل الجهد فيك*جبينه منه من عرق راشح\rيالضوء بصيرة من طرفه* الى نور داعي الهدى لامح\rانه طار فوق النجوم فضد*  ه كمعوية نابح\rان من يتوسل به فهو في*جميع امتحاناته ناجح\rولدهليز قصرك عبدك ذا*  الفقير باحداقه كاسح\rبك قوم البواهر في نعمة*  بفضلك متجرهم رابح\rوبباقة ازهار تهنية*  اليك اتى عبدك المادح', 'ح', 'ARB', '13.0', '', '', '', '2025-05-29 19:59:56', '', '0'),
(57, 65, 1, 'استمدت من الغدير البحار', 'استمدت من الغدير البحار*  فجرت من امواجها الأنـهار\nوبماء الغدير ذا كـم اراض*  سقيت كم قد انبتت اشجار\nقد جرى مع ترنم موسيقي* فتت نحو شطـه الأطـيار\nانه في رياضنا قد جرى جر*  يا لطيفا ففاحت الأزهـار\nياله منظرا جميلا اذا تلــــقي*  عليه شعاعـه الأقمـار\nولتفريج كل كرب وللتفـــريح*تشتاق نحوه الأ بصـار\nان غينا ودالا، ياء وراء*  يا حبـيـبي وراءها الأسرار\nالف لام اذ يجيئان  فاعرف* ان من ذاك تقدح الأنـوار\nكم حمام يردنه عاطشات*حين يصدرن يمتلي المنـقار\nعند هذا الغديردوح عليه*  يتغنى مذ الف عام هــزار\nمثل ماقدجرى سيجري مد* الدهـر فليست تحيله الأعـصار\nان هذا الغديرذااليوم في* ملـــكة من قد تعنو له الأبرار\nعمره طول الإله تعـالى*  فعليه تفدي لنا الأ عــمار\nانما هذه رموز،اشـارا*ت ففيها فلتمعن الأفــكار\nان اسلوب الرمزاسلوب اخ* ن الصفا قد علت لهم اقـدار\nوعلى صاحب الغدير سلام * لعن الله من هو الغــدار ', 'ر', 'ARB', '16.0', '', '', '', '2025-05-29 17:02:31', '', '0'),
(58, 68, 1, 'بلدة ذات ضياء جامنگر', 'بلدة ذات ضياء جامنگر*  برج مجد وعلاء  جامنگر\r\nان فيها قبة بدرية *  ان ارضا كسماء  جامنگر\r\nاطعمت طلاب علم الاوليأ*  وسقتهم كأس ماء  جامنگر\r\nودعاة الحق فيها سكنوا*  روضة ذات بهاء جامنگر\r\nوقبور فوقهن غلف* كسيت خير كساء جامنگر\r\nدورهم في ارضها موجودة* فغدت بحر عطاء  جامنگر\r\nوعلى ابوابها لوحاتهم* وهبت اقصى رجاء جامنگر\r\nان برهان الهدى كم زارها* فغدت ذات ثراء  جامنگر\r\nمجلس الوعظ بها يعقده* سمعت صوت بكاء جامنگر\r\nرب طول عمره طول المدى* فدعت خير دعاء جامنگر\r\nوبتسع بعدتسعين  الو* فا رأت اهل ولاء جامنگر\r\nثم كان الملتقى في سورت* فاستحقت لثناء جامنگر\r\nما الذي يفخر كاتهياوار به*  فهو من غير مراء جامنگر  ', 'ء', 'ARB', '13.0', '', '', '', '2025-06-03 17:39:47', '', 'mmmurtaza@gmail.com'),
(59, 69, 1, 'انما علم النفس علم لطيف', 'انما علم النفس علم لطيف* وبه قديقوى المريض الضعيف\rوبه يقبل الربيع الى الانـ*ـسان حقا يدبرعنه الخريف\rولسيف الهدى الحكيم الرضى في الـ*ـعلم هذا قد كثر التصنيف\rكم نفوسا بعلمه قد شفاها* زال عنها بفضله التكليف\rممتط صهوة العلى سيف دين*  وله نجله لنعم الرديف\rان برهان الدين قد حمل العلـ*ـم فينفى عنه به التحريف\rويداوي داعي الهدى بكلا الطـ*ـبين لو يأتيه الكئيب اللهيف\rوبوعظ وحكمة منه دابا*  للنفوس التهذيب والتثقيف\rمؤمن ذهنه بمسعاه صاف*ثوبه فهو طاهر ونظيف\rواتجرنا تجارة مثل ما قا* ل فاضحى في ربحنا تضعيف\rان في ذكر الثقلين لدينا*كتب منها يثقل الارشيف\rووجود الصادق مابيننامن*عند داعي الهدى لنا التشريف\rعندتبيانه ليعجبنا اسـ*ـلوبه ثم ذا الاداء اللطيف\rفليدم برهان الهدى وليـ*ـطل طو ل زمان له البقاءالشريف\rاننا قد نحب داعينااذ*حبه لا مراء دين حنيف\rلذؤيب رسالة النفس لا يفـ*ـهمها من منحاه جسم كثيف\rوبمولانا حاتم نحن لذنا*من له من مجد تليد طريف\rمرحبا بالاخوان من يمن من*لهم رأي في الامور حصيف\rانت عبدعلي تخفف عن الثقـ*ـل كمااخترت البحر وهوخفيف\r\n\r\n\r\n\rانما عـــــلــم النــفـس عــلم لـــطــــيف    وبه قـــديــــقــــوى المريـــض الضعـيف\rوبـه يـــــقـــــــــــبـل الـربـيـــــــع الى الانــــــــــــسان حـــقـــا يدبـرعـــنــــه الخـريـــــف\rولسيف الهدى الحكيم الرضى في الــــــــــعلم هـــذا قـد كـثـر الـــتـــصـــــنـيــف\rكم نـفــوسـا بــعــلـمـــه قــد شفــاهــــا    زال عـــنـهــا بـفـضـلــه الــــتكـلـيـــــف\rممــتط صــهــوة العـــلى سـيف ديــــــن     ولـــه نـــجــلـــه لـــنـــعــــم الــــرديـــــف\rان بـــرهــان الديـن قـــد حمـــل العـــلـــــــــــــم فــيـنــفى عــنـــه بـه الــتــحـــريــف\rويداوي داعي الهـــدى بـــكـلا الطــــــــــــــــبـيـن لو يأتـيـه الكــئيب اللهــــــيــــف\rوبــــوعـــظ وحــكـــمـــــة مــنــــه دابـا     للــنـــفــوس الـتـهــذيب والتــثـــقـــيــف\rمـــؤمــــن ذهـــنــه بـمــسعـــاه صـاف      ثــــوبــــــه فــهــــو طــاهــر ونـظـيـــــف\rواتجـــرنــا تجـــــارة مـــــثــــــل مـــا قـا       ل فـاضـحـى في ربـــحـنـا تضعيــف\rان في ذكــــــر الـثــقــلـيـــن لــــــــــدينــا      كــتــب مـــنــــهـا يــثــقــل الارشـيـــف\rووجــود الــصــادق مــابيــنــنــامـــــن      عــنـــد داعي الهدى لنا الـتـشــريــف\rعـنــدتــبــيـانــــه لـيــعـــــجــبـــنـا اســــــــــــــــلـــوبـــــه ثـم ذا الاداء اللـــــطـــــيــــف\rفـــليـــدم بـرهان الـــهـــدى وليـــــــــطــــل طـــو ل زمــــــــان لـــــه الـبـقــاءالشريف\rانـنـا قــــد نـــــحــب داعــــيـــــــنـااذ   حـــــــبــه لا مــــــــــراء ديـــــن حـــنـــــيــف\rلـذؤيـــب رســالـــة الــنــفــــس لا يـــــــفـــــــــــــهمهـا مـــن مـــنــحاه جسـم كثيـف\rوبـــمـــولانــا حــــاتـــم نـحـــن لــــذنــا   من لـــــه مــــن مجد تــلـيــد طـــــريـــــف\rمــــرحــبـا بالاخــــوان مـــــن يـمـــن مــــن   لهــــم رأي في الامـــور حــصــيـــف\rانت عـبدعلي تخفف عن الثــقــــل كمااخـــتـرت البحر وهـــــوخفيف', 'ف', 'ARB', '19.0', '', '', '', '2025-05-29 17:53:53', 'doubled', '0'),
(61, 72, 1, 'ميزة المرء بالبكا والضحك', 'ميزة الــمرء بالبكا  والضحك  *    فـهو طورا يضحـك طورا يبكي\nان من يضحك الاناسين في المحـ*ــفل قد فــاح فيهـم مثـل الـمسك\nانما الحزن بحــر عبدعلي يــر   *   كبـنـا مــن فـكاهــة في فــــلك\nقد اكلنا تـنجن متنـجن له قـو   *  ل ظــريـــف كلـؤلـؤ في سلـك\nقـوله  في الصلوة هـاز وغــاز   *   في طعام  يضــحك من غير شـك\nلا تكن يوما قمطريرا عبوسا  *  اف يوآرلاف ايوري دسس يوآرويري لكي\nان تفاكه من ضمهم محفل صا    *    روا سكارى من دون شرب الوسكي\nآ ج كال ححوها ككهنا؛ يعني *كه كمثثيوتر نا اوثثر ككهنا ؛ ماؤس مـكي\nصاف دل سي مذاق كرجو اسس* مارا دوستو كوئي ني نا اتارجـو ثثهركي\nتهندو ثثاني بهي ناكهتا آوروو جوئيه * جو تماراسي كوئي بهي جائسس بهركي\nروجو هردم حسين ثثر انسس عبا * س نا اوثثر جه لئي نسس آيا تـهـا مــشـكي\nمكة او مدينة حـضـرة الدا  *    عـي فــطــوبى لمــــدني  ومـــــكي\nليلة القدر آثث ؛ همنسس آ صا  *  دق جككاوسس ؛ علم نو ثثاني ححـهـرٌكي\nآج عــبــدعــــلي غـــلام نسس ايسسس *\nشعر لكهسس طبيعت سب كي هسس ثثهرٌكي\nطــول الله عـــمر برهـان دين   *    الله فهــو المنجـي لنا مــــن هلك', 'ك', 'ARB', '15.0', '', '', '', '2025-05-29 18:04:35', '', '0'),
(62, 74, 1, 'امامنا جعفر الصادق', 'امامنا جعفر الصادق* لصبح اسلام هوالفالق\rقال الصلوة اصله انما* الزكوة فهي فرعه الباسق\rان الجهاد ذروة لسنا* مه فذاك العلم الشاهق\rففي الصلوة نذكرالله مع*  معرفة بانه الخالق\rوفي الزكوة نحن معترفو* ن انما الله لنارازق\rمجاهد في الله بالبذل للـ*ـنفس فبالله هو الواثق\rقدصدق الامام الطهرفللا* سلام هذا وصفه اللائق\rوانما فقها كذا ادبا* لاهل ذوق قوله رائق\rلديه شرع سره واضح*  فانه الفاتق والراتق\rمن ترك الاسلام اصلا وفر* عا ذروة فانه المارق\rواسرق السراق من في صلو*ته وفي حدودها سارق\rالصادق السابق برهان ديـ*ـن الله في الصدق له اللاحق\rوطيب هذا الصادق الفردمن  ردن محمد لنا عابق\rويذكر الحسين ذكرا كثيـ*ـرا دائما فهو له عاشق\rمعراجه دابا ويأتيه من*  امامه تائيده البارق\rلنا شفاء كل داء به*فانه طبيبنا الحاذق\rعدوه كمثل ابليس دو*رآدم  عن امره فاسق\rطول الهي عمره ما نسيـ*ـم الصبح مشيا ينشق الناشق\rعلى بني طه وطه سلا*م من بوحي ربه ناطق\rاغتنم الحيوة عبدعلي * للموت يوما انك الذائق\r\r\rمن بيان سيدنا طع  1924 بعد الظهر\r\n\r\n\r\n\r\n\r\n\r\n\rامـامـنـا جـعـفـر الـصـادق    لـصـبــح اسـلام هوالــفــالــق\rقـال الصـلوة اصـلـه انـمــا    الزكــوة فهـي فـرعه البـاسـق\rان الجــهــاد ذروة لـــسـنا    مـه فذاك الـعـلـم الشــاهــــق\rففي الصلـوة نذكرالله مع     مـعـرفـــة بانـــــه الـخـــالــــــق\rوفي الزكوة نـحـن معـتـرفو    ن انـــمـــــا الله لــــنــــــارازق\rمجاهد في الله بالبذل للــــــــــــنفس فبالله هــــو الـــواثــــــق\rقـدصدق الامام الطهرفللا    سلام هــذا وصفه اللائــــق\rوانـمـــا فــقــهــــا كــذا ادبـا    لاهــل ذوق قـــولـه رائــــــق\rلديـه شـرع سـره واضــــح     فانه الفــاتـق والــــراتـــــــــق\rمن ترك الاسلام اصلا وفر    عا ذروة فــانــه الـمــــارق\rواسرق السراق من في صلو   ته وفي حـدودها ســارق\rالصادق السابق برهان ديـــــــن الله في الصدق له اللاحق\rوطيب هذا الصادق الفردمن  ردن محــمـــد لـنـا عـــابــق\rويذكر الحسيـن ذكـرا كـثــيــــــــــــرا دائمـا فهـو لـــه عـاشـق\rمعراجـه دابا ويأتـيـه مـــــن     امامــــه تائـيــــده الـــبــارق\rلنا شــــفــــاء كــل داء بــه      فانــه طــبـيـبـنـا الحـــــــاذق\rعــــــدوه كمثـل ابليس دو      رآدم  عــــن امــره فــاسق\rطول الهي عــمــره مـا نسيـــــم الصبح مشيا ينشق الناشق\rعلى بني طـــه وطــه سلا      م من بوحي ربـــــه نـــاطـق\rاغتنم الحيوة عبــدعلي\rللموت يوما انك الذائق', 'ق', 'ARB', '20.0', 'من بيان سيدنا طع  1924 بعد الظهر', '', '', '2025-05-29 17:31:18', '', '0'),
(63, 76, 1, 'عربي وفارسي واردو', '\rعــــربي وفــــارسي واردو    كل لسـن في نـــوعــه هـــو فـرد\rعــربي ام وهــاتـــان بــــنــتـا    ن لها مــنــهــن تـكـثــر ولـــــــد\rان هذي اللغات غيدوشعر    الشعرا في اجيادهـن العـــقــــد\rان فيهامـع كـثـرة وحدة في الـــــــحسن بعض من بعضه يستمد\rفالبراعات حسن كل اديب     فهو في فهم فطــرة مـســتعـــــد\rانهم قد تــغزلوابعـضـهم يـــلـــــــــــــهيه ساق والبعض يلهيه خـــد\rولكل ذوق فكم ناقـدمـــنــــــــــــــهم اليهم قد جاء نقدفنــقـــــد\rمطلعا مــع تخـلــص وخـتـام     من يصب فهو بارعا قـد يعد\rاذ ارادواتـخـلـصامن نسيب    نحومدح الممدوح كـان المـــرد\rادب فاطـــــمي فيه البــراعـا    ت فلا هزل فيها بل هو جــد\rان بين الآداب اجمــعــها يــــعــــــــلو لذا الادب المهذب جــــــد\rفله لا تخلـــص  بل له تــــو      ضيح ما قبل بالذي هــو بعد\rولدينا شعـر الامـــيــرتميـــم     ان ممدوحه المعـــــــــز معــــــــد\rذكره في النشبيب محبوبه فهــــــــو لممدوحـــــه وذلك قـصـــــد\rذكره للمعشوق رمز كذاك     الخمر رمز لا يفهم الرمز وغـد\rلوصال في ادب فاطــمــــي     خير رمز لعارفيــــه الـوجـــــد\rان اعلام الادب الفاطمي الطهـر فضل لهم معـــلى ومــــجــــد\rان ممدوحهم تخــــلصهم من    ذي الهيولى بــه وذلك سعــــــد\rولعبد الله فخر الدين براعا  ت كثيرات وهي ليست تحــد\rولعبدعلي ثم لــنـجـــم الــــــــــــدين تشبيب كله هــو رشـــد\rان برهان الدين محبوبنا مـن   دائما حبنــا لــــــــــه والـــــــود\rفاطال الاله عمرك يدعو  لك بعد الصلوة هذا العبد\r\r\r\r\r\r\r\r\rعــــربي وفــــارسي واردو    كل لسـن في نـــوعــه هـــو فـرد\rعــربي ام وهــاتـــان بــــنــتـا    ن لها مــنــهــن تـكـثــر ولـــــــد\rان هذي اللغات غيدوشعر    الشعرا في اجيادهـن العـــقــــد\rان فيهامـع كـثـرة وحدة في الـــــــحسن بعض من بعضه يستمد\rفالبراعات حسن كل اديب     فهو في فهم فطــرة مـســتعـــــد\rانهم قد تــغزلوابعـضـهم يـــلـــــــــــــهيه ساق والبعض يلهيه خـــد\rولكل ذوق فكم ناقـدمـــنــــــــــــــهم اليهم قد جاء نقدفنــقـــــد\rمطلعا مــع تخـلــص وخـتـام     من يصب فهو بارعا قـد يعد\rاذ ارادواتـخـلـصامن نسيب    نحومدح الممدوح كـان المـــرد\rادب فاطـــــمي في