(541, NULL, NULL, 'بجا هے فخر كرے خود په هي اگر لندن', 'بجا هے فخر كرے خود په هي اگر لندن * كه آقا برھان ديں شاه كا هے گھر لندن\r\nهر ايك اير ميں اسكي هي بات كرتا تھا * يه سچ هے هو گيا سب كا هي هم سفر لندن\r\nاندھيرا دور هوا روشني هے پھيلي هوئي * كچھ اسطرح سے هوا مطلع القمر لندن\r\nتمام مؤمنيں ليتے هيں خوب هي لذت * كه ايك ميٹھا سا گويا كه هے ثمر لندن\r\nهوا ے منتخب مولى كي سالگره كے لئے * كه دھيرے دھيرے هوا اتنا با اثر لندن\r\nمبارك مولى مبارك تجھے يه سالگره * يه عرض كرتا هے خم كركے اپنا سر لندن\r\nتري هي نظر كرم سے بلنديوں پر هے * تو زير اس ميں نهيں هو گيا زبر لندن\r\nهيں آئيصاحبه تو مبتدا كرم كے ترے * يه مبتدا كي هميں ديتا هے خبر لندن\r\nاے آقا باقي رهو آپ بس قيامت تك * دعاء يه لاتا هے هر دم زبان پر لندن\r\nهميشه لطف رها تجھ په شه كا عبدعلي\r\nاسي لئے تو بنا تيري رهگذر لندن', 'ر', 'URD', '10.0', '', '', 'milad', '2025-06-01 19:51:42', '', ''),
(542, NULL, NULL, 'عجب همارا تو خيمه هے يه رياضت كا', 'عجب همارا تو خيمه هے يه رياضت كا * يهاں په اندازه هوتا هے سب كي طاقت كا\r\nهر ايك ٹيم يهاں آكے اپنے ميداں ميں * كرا رهي هے وه درشن هميں مهارت كا\r\nكھے جو ريفري يا كه كھے جو ايمپاير * قبول كرتے هيں يه بھي سبق هے طاعت كا\r\nخدا كے داعي كي طاعت هے فرض اور واجب * هميں يقين هے يه شاه كي شفاعت كا\r\nمبارك اسكو ملا آج جسكو هے انعام * مبارك كه وه هوا مستحق كرامت كا\r\nهم سب هيں جامعه والے هيں هم تو جند الله * شرف ملا هے ابو القائد كي قيادت كا\r\nهم ورزشوں سے بدن تندرست ركھتے هيں * كه جس سے لطف زياده رهے عبادت كا \r\nهے 102 ويں يه ميلاد آقا مولى كي * هے جشن هر اك جگه آپ كي ولادت كا \r\nجسے وه حره كو مولانا آمر نے بھيجا * اے مولى تو هي هے والله سجل بشارت كا\r\n هم شكر الله تعالى كا كرتے هيں هر آں * كه همنے ديكھا ترا دور يه سعادت كا\r\n لكھا تھا طاهر نے عالي قدر مفضل جب * كھلا هے هم په تو اب راز وه اشارت كا\r\n خدا كےحكم سے قائم هيں ناص اور منصوص * خدا نے ذمه ليا دونوں كي حفاظت كا\r\n تو لكھتا آيا هے دونوں كي مدح عبدعلي\r\n تو رهنا پابند هميشه يه اپني عادت كا', 'ت', 'URD', '13.0', '', '', '', '2025-06-01 19:59:02', 'khaimat', 'mmmurtaza@gmail.com'),
(543, NULL, NULL, 'هر دور ميں شهيد كربل كا غم هوا هے', 'هر دور ميں شهيد كربل كا غم هوا هے\r\nشبير كا يه قر آ ں كو بھي الم هوا هے\r\n\r\nصديوں سے آ دميت آ نسوں بها رهي هے\r\nكرب و بلا ميں ايسا ظلم و ستم هوا هے\r\n\r\nهے عرش و كرسي لرزاں روتا هے آسماں بھي\r\nجب ذبح زير خنجر شا ه امم هوا هے\r\n\r\nدو بازو كو كٹا كر عبا س گر گئے هيں\r\nليكن و فا كا ان سے اونچا علم هوا هے\r\n\r\nبرھا ن دين مو لى كي سعي دائمي سے\r\nمؤ من طر يق حق پر ثا بت قدم هوا هے\r\n\r\nمو لى كے ساتھ ما تم پر جوش هم كرينگے\r\nاس واسطے هما را جگ ميں جنم هوا هے\r\n\r\nهم سب كو ذاكريں كي صف ميں بٹھا دئےهيں\r\nا د نى غلا مو ں پر مولى كا عجب كر م هے\r\n\r\nوا جب هےشكر تيرا هم پر اے مولى زيا د ه\r\nليكن اد ا بهت هي هم سے وه كم هوا هٍے\r\n\r\nاے عا شر مدبر تير ے بيا ن هي سے\r\nاپنے گنا هو ں پر هم سب كو ند م هوا هے\r\n\r\nطو ل البقا هو تيري بس يه دعا كے خا طر\r\nعبدعلي كا هر دم جا ر ي قلم هوا هے', 'م', 'URD', '10.0', '', 'marsiyah', '', '2025-06-01 20:01:00', '', 'mmmurtaza@gmail.com'),
(544, NULL, NULL, 'فضيلت تيري مولى ظاهره هے', 'فضيلت تيري مولى ظاهره هے * ترا هر معجزه بھي باهره هے\r\nگذشته سال قبه حاتميه * تھا اور اس سال روضه طاهره هے\r\nيمن اور هند ميں تو حاتمي طا * هري صورت يه شه كي زاهره هے\r\nيه برھان الھدى سے وه اماميه * حضرت سارے جگ ميں حاضره هے\r\nاے مولى وعظ تيري ياد ركھنے * هميں دي رب نے قوت ذاكره هے\r\nترے ديدار كر نے كے لئے بس * خدا نے دي يه قوت باصره هے\r\nاے مولى جامعه سيفيه هردم * تري سب نعمتوں پر شاكره هے\r\nاور آيا شكر كر نے اداره * اور اسكے ساتھ هر اك دائره هے\r\nتري خدمت ميں حاضر حاديه * عشره هے اور حاضر عاشره هے\r\nرهو با قي ھزارو سال مولى 	 * خدا نے سلطنت دي قاهره هے', 'ر', 'URD', '10.0', '', '', '', '2025-06-01 20:05:09', '', 'mmmurtaza@gmail.com'),
(545, NULL, NULL, 'اے امام ابن آمر ترا عالي هے گھرانه', 'اے امام ابن آمر ترا عالي هے گھرانه * ترے واسطے بنا هے يه جهاں كا كارخانه\r\nجو تھے اروى بنت احمد كھلے راز سارے ان پر * جب نظر نے انكي ديكھا تھا رومال وه پرانه\r\nهوا جب ترا عقيقه تھا مزين مصر سارا	* تجھے عهد طفلي ميں هي ملي شان هے شهانه\r\nاے امام تو سليماں هے دعاه هيں پرندے	*	يه پرندوں سے هے آباد ترا جگ ميں آشيانه\r\nهيں محمد داعي حق يه تصور هے هماري	*	كه هے انكا آستانه تو امامي آستانه\r\nتو علي كا داعي هے مرغابياں هيں مؤمنيں سب *	تو هميشه انكا ركھتا هے خيال آب و دانه\r\nرهے باقي شادماں تو ترے گھر ميں شادياں هوں * بجے هر گھڑي حويلي په تمهاري شاديانه\r\nميں هوں عبد ادنى تيرا هوں ميں جامعه كا خادم\r\nتري جامعه كا هردم مرے لب په هے ترانه', 'ن', 'URD', '8.0', '', '', '', '2025-06-01 20:10:39', '', 'mmmurtaza@gmail.com');
